

Add-Type -AssemblyName System.Windows.Forms
[System.Windows.Forms.Application]::EnableVisualStyles()

$MainForm                        = New-Object system.Windows.Forms.Form
$MainForm.ClientSize             = New-Object System.Drawing.Point(298,200)
$MainForm.text                   = "More"
$MainForm.TopMost                = $false

$iconBase64                      =  ''
$iconBytes                       = [Convert]::FromBase64String($iconBase64)
$stream                          = New-Object IO.MemoryStream($iconBytes, 0, $iconBytes.Length)
$stream.Write($iconBytes, 0, $iconBytes.Length)
$MainForm.Icon                    = [System.Drawing.Icon]::FromHandle((New-Object System.Drawing.Bitmap -Argument $stream).GetHIcon())

$et                              = New-Object system.Windows.Forms.Button
$et.text                         = "Extra Tweaks"
$et.width                        = 156
$et.height                       = 30
$et.location                     = New-Object System.Drawing.Point(123,88)
$et.Font                         = New-Object System.Drawing.Font('Microsoft Sans Serif',9)

# $Button1                         = New-Object system.Windows.Forms.Button
# $Button1.text                    = "Extra Tools"
# $Button1.width                   = 83
# $Button1.height                  = 30
# $Button1.location                = New-Object System.Drawing.Point(11,55)
# $Button1.Font                    = New-Object System.Drawing.Font('Microsoft Sans Serif',8)

$fet                             = New-Object system.Windows.Forms.Button
$fet.text                        = "Extra Tools"
$fet.width                       = 156
$fet.height                      = 30
$fet.location                    = New-Object System.Drawing.Point(123,55)
$fet.Font                        = New-Object System.Drawing.Font('Microsoft Sans Serif',10)

$vbox                            = New-Object system.Windows.Forms.Button
$vbox.text                       = "VirtualBox"
$vbox.width                      = 83
$vbox.height                     = 30
$vbox.location                   = New-Object System.Drawing.Point(11,54)
$vbox.Font                       = New-Object System.Drawing.Font('Microsoft Sans Serif',11)

$Label1                          = New-Object system.Windows.Forms.Label
$Label1.text                     = "Install"
$Label1.AutoSize                 = $true
$Label1.width                    = 25
$Label1.height                   = 10
$Label1.location                 = New-Object System.Drawing.Point(11,8)
$Label1.Font                     = New-Object System.Drawing.Font('Microsoft Sans Serif',24)

$Label2                          = New-Object system.Windows.Forms.Label
$Label2.text                     = "Essentials"
$Label2.AutoSize                 = $true
$Label2.width                    = 25
$Label2.height                   = 10
$Label2.location                 = New-Object System.Drawing.Point(126,8)
$Label2.Font                     = New-Object System.Drawing.Font('Microsoft Sans Serif',24)

$MainForm.controls.AddRange(@($Label1,$Label2,$et,$Button1,$fet,$vbox))

$vbox.Add_Click({
    Write-Host "Installing VirtualBox"
    Write-Host "Installing VirtualBox... Please Wait" 
    # winget install -e Oracle.VirtualBox | Out-Host
    if($?) { Write-Host "Installed VirtualBox" }
    Write-Host "Finished Installing VirtualBox" 
    Write-Host "Ready for Next Task"
})


    
[void]$MainForm.ShowDialog()    